function url() {

	$base_dir = __DIR__;
	
	// server protocol
	$protocol = empty($_SERVER['HTTPS']) ? 'http' : 'https';

	// domain name
	$domain = $_SERVER['SERVER_NAME'];

	// server port
	$port = $_SERVER['SERVER_PORT'];
	$disp_port = ($protocol == 'http' && $port == 80 || $protocol == 'https' && $port == 443) ? '' : ":$port";

	// put em all together to get the complete base URL
	$url = "${protocol}://${domain}${disp_port}";
	$url .= '/Hamit';

	return $url; // = http://example.com/path/directory

}

function pUrl() {
	echo url();
}

function redirect($str) {
	header("Location: " . url() . "${str}");
}

function formatDate($dateString) {

	# Ex: 11/20/2017 to 2017-11-20 as DATE in Sql
	$yyyy 	= substr($dateString, 6);
	$mm 	= substr($dateString, 0, 2);
	$dd 	= substr($dateString, 3, -5);

	return "${yyyy}-${mm}-${dd}";

}

function printPost($needle) {
	if (isset($_POST[$needle])) {
		echo $_POST[$needle];
	}
}

